let navbar=document.querySelector(".navbar"),navbarToggle=navbar.querySelector(".navbar-toggle");function openMobileNavbar(){navbar.classList.add("opened"),navbarToggle.setAttribute("aria-label","Close navigation menu.")}function closeMobileNavbar(){navbar.classList.remove("opened"),navbarToggle.setAttribute("aria-label","Open navigation menu.")}navbarToggle.addEventListener("click",()=>{navbar.classList.contains("opened")?closeMobileNavbar():openMobileNavbar()});let navbarMenu=navbar.querySelector(".navbar-menu"),navbarLinksContainer=navbar.querySelector(".navbar-links");navbarLinksContainer.addEventListener("click",a=>{a.stopPropagation()}),navbarMenu.addEventListener("click",closeMobileNavbar)